'use strict';console.log("Kalender");let days=document.querySelector(".days"),currentDayEl,selectedDay=-1,savedAppointments=JSON.parse(localStorage.getItem("appointments"));console.log(savedAppointments);let time=document.querySelector("#time");for(let a=1;32>a;a++){const b=document.createElement("li");b.innerHTML=a;days.appendChild(b);b.addEventListener("click",()=>{currentDayEl&&currentDayEl.classList.remove("active");setDay(a);selectedDay=a;b.classList.add("active");currentDayEl=b})}
function setDay(a){console.log(a);document.querySelector("#currentDate").innerHTML=a+" M\u00e4rz 2021"}let nameInput=document.querySelector("#name");const button=document.querySelector(".newApp");button.addEventListener("click",()=>{let a=nameInput.value,b=time.options[time.selectedIndex].value;console.log(b+" "+selectedDay+" "+a);savedAppointments.push(b+" "+selectedDay+" "+a);localStorage.setItem("appointments",JSON.stringify(savedAppointments))});